/**
 * This script processes items from a data source like Airtable.
 * It cleans up the data by:
 * 1. Parsing stringified JSON fields ('Author', 'Content', 'Stats', 'Hashtags') into actual objects.
 * 2. Converting all keys with spaces or dashes into a standard camelCase format (e.g., "Post URL" becomes "postUrl").
 * 3. Specifically formatting the keys within the parsed 'Stats' object to camelCase.
 * This makes the data much easier and more reliable to work with in subsequent n8n nodes.
 */

// Helper function to convert any string to camelCase.
// Example: "Post URL" -> "postUrl", "total\_reactions" -> "totalReactions"
const toCamelCase = (str) => {
  // Replace a potential backslash followed by a space, underscore, or dash
  // and a letter with the uppercase version of that letter.
  const spacedCamel = str.replace(/\\?[\s_-]+(\w)/g, (_, c) => c.toUpperCase());
  return spacedCamel.charAt(0).toLowerCase() + spacedCamel.slice(1);
};

// An array of keys that are expected to contain stringified JSON.
const keysToParse = ['Author', 'Content', 'Stats', 'Hashtags'];
const formattedItems = [];

// Loop through each item provided by the previous n8n node.
for (const item of $items()) {
  const cleanedJson = {};

  // Loop through every key-value pair in the current item's JSON data.
  for (const [key, value] of Object.entries(item.json)) {
    const newKey = toCamelCase(key);
    let newValue = value; // Default to the original value.

    // If the key is in our list of keys to parse AND the value is a non-empty string...
    if (keysToParse.includes(key) && typeof value === 'string' && value.trim() !== '') {
      try {
        // ...attempt to parse the string into a real JavaScript object.
        newValue = JSON.parse(value);

        // Specifically format the keys within the 'Stats' object after parsing.
        if (key === 'Stats' && typeof newValue === 'object' && newValue !== null && !Array.isArray(newValue)) {
          const formattedStats = {};
          for (const [statsKey, statsValue] of Object.entries(newValue)) {
            formattedStats[toCamelCase(statsKey)] = statsValue;
          }
          newValue = formattedStats; // Replace with the object that has camelCased keys
        }
      } catch (error) {
        // If parsing fails, log a warning and keep the original string value.
        // This prevents the entire workflow from failing on one bad piece of data.
        console.warn(`Could not parse JSON for key "${key}" in item with id "${item.json.id}". Keeping original value.`);
        newValue = value;
      }
    }

    // Add the cleaned key and its corresponding value to our new object.
    cleanedJson[newKey] = newValue;
  }

  // Add the fully cleaned object to our results array, wrapped in n8n's expected format.
  formattedItems.push({ json: cleanedJson });
}

// Return the array of cleaned items for the next node in the workflow.
return formattedItems;

// Get all items coming into this node (from the GPT node)
const gptItems = $input.all();

// Get all items from the 'Switch' node by its name
const switchItems = $('Switch').all();

// Extract just the 'text' from each GPT item's JSON payload.
// We are NOT filtering out empty strings to maintain a 1-to-1 match.
const gptDescriptions = gptItems
  .map(item => item.json.text);

// Check if the number of items from both sources match.
if (switchItems.length !== gptDescriptions.length) {
  throw new Error(`Item count mismatch: Switch node has ${switchItems.length} items, but GPT node has ${gptDescriptions.length}. Cannot map 1-to-1.`);
}

// Loop through the Switch items and add the corresponding GPT description
// from the same position (index) in the gptDescriptions array.
return switchItems.map((item, index) => {
  return {
    json: {
      ...item.json, // Keep original data from the Switch item
      gpt_description: gptDescriptions[index] // Add the single GPT description (can be empty)
    }
  };
});

// Get all GPT outputs into an array
const gptOutputs = $input.all().map(item => item.json.content);

// Loop through all Switch posts and use the index to pair them
return $('Switch').all().map((item, index) => {
  const switchPost = item.json;

  return {
    json: {
      ...switchPost,                      // keep original Switch post data
      gpt_description: gptOutputs[index] // attach the SINGLE corresponding GPT output
    }
  };
});

