/**
 * Collects all `content` fields from the items
 * and assigns them to keys post1, post2, post3, ...
 */

const output = {};

for (const [i, item] of $items().entries()) {
  if (item.json && item.json.message && item.json.message.content) {
    output[`post${i + 1}`] = item.json.message.content;
  }
}

// Return one object with post1, post2, post3, etc.
return [
  {
    json: output
  }
];

// Get all GPT outputs into an array
const gptOutputs = $input.all().map(item => item.json.content);

// Loop through all Switch posts and use the index to pair them
return $('Switch').all().map((item, index) => {
  const switchPost = item.json;

  return {
    json: {
      ...switchPost,                      // keep original Switch post data
      gpt_description: gptOutputs[index] // attach the SINGLE corresponding GPT output
    }
  };
});

// Get the raw output text
const rawOutput = $json.output || "";

// Match all ```json ... ``` blocks
const regex = /```json([\s\S]*?)```/g;

let results = [];
let match;

while ((match = regex.exec(rawOutput)) !== null) {
  let block = match[1].trim();

  // --- 1Ô∏è‚É£ Fix double-brace style: { { ... }, { ... } }  ----
  if (/^\{\s*\{[\s\S]*\}\s*\}$/.test(block)) {
    block = "[" + block.replace(/^\{\s*/, "").replace(/\s*\}$/, "") + "]";
  }

  // --- 2Ô∏è‚É£ Handle multiple standalone objects {..}{..}{..} ---
  if (!block.startsWith("[") && /\}\s*\{/.test(block)) {
    block = "[" + block.replace(/\}\s*\{/g, "},{") + "]";
  }

  try {
    const parsed = JSON.parse(block);

    if (Array.isArray(parsed)) {
      parsed.forEach(obj => {
        if (obj.comments && Array.isArray(obj.comments)) {
          // flatten comments array into separate objects
          obj.comments.forEach(c => results.push(c));
        } else {
          results.push(obj);
        }
      });
    } else {
      if (parsed.comments && Array.isArray(parsed.comments)) {
        parsed.comments.forEach(c => results.push(c));
      } else {
        results.push(parsed);
      }
    }

  } catch (e) {
    try {
      const wrapped = JSON.parse(`[${block}]`);
      wrapped.forEach(obj => results.push(obj));
    } catch (_) {
      // skip invalid chunks
    }
  }
}

// ‚úÖ Return each object as its own JSON item
return results.map(obj => ({ json: obj }));
