// Clean website URL to extract root domain for Anymail Finder API
const website = $input.item.json.website;

if (!website) {
  return { domain: '' };
}

try {
  const url = new URL(website);

  // Remove www. and extract hostname
  let hostname = url.hostname.toLowerCase();
  hostname = hostname.replace(/^www\./i, '');

  // Extract root domain (last two parts)
  const parts = hostname.split('.');
  const domain = parts.slice(-2).join('.');

  return { 
    domain: domain,
    original_website: website,
    row_number: $input.item.json.row_number,
    companyName: $input.item.json.companyName,
    phone: $input.item.json.phone
  };
} catch (error) {
  return { 
    domain: '',
    error: 'Invalid URL',
    original_website: website 
  };
}

// Remove HTML tags and extract plain text
const items = [];

for (const item of $input.all()) {
  let htmlContent = item.json.data || '';

  // Remove script and style elements
  htmlContent = htmlContent.replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '');
  htmlContent = htmlContent.replace(/<style\b[^<]*(?:(?!<\/style>)<[^<]*)*<\/style>/gi, '');

  // Remove HTML comments
  htmlContent = htmlContent.replace(/<!--[\s\S]*?-->/g, '');

  // Remove all remaining HTML tags
  let plainText = htmlContent.replace(/<[^>]+>/g, ' ');

  // Clean up whitespace
  plainText = plainText.replace(/\s+/g, ' ').trim();

  // Limit text length for API efficiency
  plainText = plainText.substring(0, 5000);

  // Pass through all needed data
  items.push({
    json: {
      plainText: plainText,
      email: $('Anymail Finder - Get Decision Maker Email').item.json.result.email,
      personFullName: $('Anymail Finder - Get Decision Maker Email').item.json.result.personFullName,
      companyName: $('Loop Over Items').item.json.companyName,
      website: $('Loop Over Items').item.json.website,
      phone: $('Loop Over Items').item.json.phone,
      row_number: $('Loop Over Items').item.json.row_number
    }
  });
}

return items;

// Parse email outputs and prepare for Instantly.ai
const items = [];

for (const item of $input.all()) {
  // Get the OpenAI responses
  const email1Response = $('Write Cold Email').item.json.choices[0].message.content || '';
  const email2Response = $('Write Cold Email1').item.json.choices[0].message.content || '';
  const email3Response = $('Write Cold Email2').item.json.choices[0].message.content || '';

  // Parse Email 1 (has subject and body)
  let email1Subject = '';
  let email1Body = '';
  if (email1Response.includes('SUBJECT:')) {
    const parts = email1Response.split('BODY:');
    email1Subject = parts[0].replace('SUBJECT:', '').trim();
    email1Body = parts[1]?.trim() || '';
  }

  // Email 2 and 3 are body only
  const email2Body = email2Response.replace('BODY:', '').trim();
  const email3Body = email3Response.replace('BODY:', '').trim();

  items.push({
    json: {
      email: item.json.email,
      firstName: item.json.firstName,
      lastName: item.json.lastName,
      cleanCompanyName: item.json.cleanCompanyName,
      personalization: item.json.personalization,
      phone: item.json.phone,
      website: item.json.website,
      row_number: item.json.row_number,
      'Email 1-Subject': email1Subject,
      'Email 1-Body': email1Body,
      'Email 2-Subject': '',
      'Email 2-Body': email2Body,
      'Email 3-Subject': '',
      'Email 3-Body': email3Body
    }
  });
}

return items;
